var searchLink=document.querySelector(".search-hotel-form-title"),searchForm=document.querySelector(".search-hotel-form");function changeState(){searchForm.classList.toggle("form-hidden")}function checkFieldValid(e){return!!e.value||(fieldNotValid(),!1)}function fieldNotValid(){searchForm.classList.remove("form-error"),searchForm.offsetWidth=searchForm.offsetWidth,searchForm.classList.add("form-error")}searchLink.onclick=function(e){changeState(),e.preventDefault()},searchForm.addEventListener("submit",function(e){checkFieldValid(document.getElementById("checkin-date"))&&checkFieldValid(document.getElementById("checkout-date"))&&checkFieldValid(document.getElementById("adults"))||e.preventDefault()}),changeState();
